<?php$date = date('d');$generate = mysql_fetch_assoc(mysql_query("SELECT * FROM $server_db.motd WHERE id = 1"));$view = $generate['image'];$day = $generate['day'];if($day != $date){$select = mysql_fetch_assoc(mysql_query("SELECT * FROM $server_db.media WHERE visible != 0 && type != 0 && type != 5 ORDER BY RAND() LIMIT 1"));if($select['id'] != $view)					{				mysql_query("UPDATE $server_db.motd SET `image` = '". $select['id'] . "', `day` = '" . $date . "' WHERE id = 1");					}else					{				echo '<!-- <meta http-equiv="refresh" content="0;"> -->';					}				}$media = mysql_fetch_assoc(mysql_query("SELECT * FROM $server_db.media WHERE id = '" . $view . "'"));?><div class="sidebar-module" id="sidebar-sotd"><div class="sidebar-title"><h3 class="title-sotd"><a href="">Image of the Day</a></h3></div><div class="sidebar-content"><div class="sotd" style="background-image: url(<?php echo $media['link']; ?>);"><a href="media/images_visor.php?type=<?php echo $media['type'] ?>/<?php echo $media['id'] ?>#/<?php echo $media['id'] ?>" class="image"> </a><div class="caption"><a href="media/" class="view">All Media</font></a><a href="media/send_media.php" class="submit">Submit Media</a><span class="clear"><!-- --></span></div></div></div></div>